    private void btnCalcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcActionPerformed
        // TODO add your handling code here:
        int num = Integer.parseInt(spinner.getValue().toString());
 
        /* Calcular o RESTO por 2*/
        int resto = num % 2;
        lblResto.setText(Integer.toString(resto));
        
        /* Calcular ELEVADO ao Cubo */
        int cubo = (int) Math.pow(num,3);
        lblCubo.setText(Integer.toString(cubo));
        
        /* Calcular a RAIZ QUADRADA */
        float quadrada = (float) Math.sqrt(num);
        lblRaizQ.setText(Float.toString(quadrada));
        
        /* Calcular a RAIZ CUBICA */
        float cubica = (float) Math.cbrt(num);
        lblRaizC.setText(Float.toString(cubica));
        
        /* Calcular o VALOR ABSOLUTO */
        float absoluto = (float) Math.abs(num);
        lblAbs.setText(Float.toString(absoluto));
    }//GEN-LAST:event_btnCalcActionPerformed

